       const std::clock_t _start_time = std::clock();
       const double _run_time = (double)(std::clock() - _start_time)/CLOCKS_PER_SEC;

       Neuron flow:
       init():
         V = reset
         fired = 0
         t = 0
       fire():
           send spike
           if V > thr:
               payload = 1
           V = reset
           fired = 1
       spike():
         if incoming message:
           V += weight

InputPin.OnReceive:
          deviceState->v=deviceProperties->v_init;
	  deviceState->t=0;

          handler_log(3, "INIT: t=%f, v=%f", deviceState->t, deviceState->v);

OutputPin.OnSend:
	  float &v=deviceState->v;
          float &t=deviceState->t;
  float _lio_1 = - deviceProperties->dt;
	  float v_expr = (deviceProperties->I + (v * exp(_lio_1 / deviceProperties->tau))) - (deviceProperties->I * exp(_lio_1 / deviceProperties->tau));

          v = v_expr;

          t += deviceProperties->dt;

          handler_log(3, "Fire: t=%f, v=%f", deviceState->t, deviceState->v);
          message->time = t;
          if(deviceState->v >= deviceProperties->thr){ 
              message->fired = true;          
              deviceState->v=deviceProperties->rst;
              handler_log(1, "FIRE!");
          } else {
              message->fired = false;
          }

InputPin.OnReceive:
if(message->fired){
    deviceState->v += edgeProperties->weight;
}
handler_log(3, "Incoming: t_sent=%f, t_recv=%f", message->time, deviceState->t);
