       const std::clock_t _start_time = std::clock();
       const double _run_time = (double)(std::clock() - _start_time)/CLOCKS_PER_SEC;

       Neuron flow:
       init():
         V = reset
         fired = 0
         t = 0
         waitTick = 0
       fire():
         if v > thr and not waitTick:
           send spike message
           V = reset
           fired = 1
       tock(): 
         if not waitTick:
           t += dt
           fired = 0
           waitTick = 0
       spike():
         if incoming message:
           V += weight

InputPin.OnReceive:
      deviceState->v=deviceProperties->v_init;
deviceState->t=0.0;
      deviceState->waitTick=false;
      deviceState->sentSpike=true; //don't fire in round 1
      handler_log(1, "INIT v=%f v_init=%f", deviceState->v, deviceProperties->v_init);

OutputPin.OnSend:
assert(!deviceState->waitTick);

if (deviceState->t >= deviceProperties->endTime){
    handler_exit(0);
}

float &v = deviceState->v;

